package quiz;

public class Quiz8 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		float b = 2.0f;
		int 더하기 = a + (int)b;
		int 곱하기 = a*(int)b;
		int 나누기 = a / (int)b;
		int 빼기 = a-(int)b;
		System.out.println(더하기);
		System.out.println(곱하기);
		System.out.println(나누기);
		System.out.println(빼기);
		
	}

}
